                    const outerSq = entry.outerRadiusNDC * entry.outerRadiusNDC;
                    const innerSq = entry.innerRadiusNDC * entry.innerRadiusNDC;
                    if (distSq <= outerSq && distSq >= innerSq) {
                        hits.push({
                            distance: entry.distance,
                            point: entry.worldPosition.clone(),
                            object
                        });
                    }
                    return;
